SELECT DISTINCT L.ALUNO, L.MOTIVO, L.ANO_ENCERRAMENTO, L.SEM_ENCERRAMENTO
FROM LYCEUM..LY_H_CURSOS_CONCL L
JOIN #TEMP_DIREITO T ON T.ALUNO = L.ALUNO
WHERE L.DT_REABERTURA IS NULL
ORDER BY 1




