--DROP TABLE TMP_DISC_TURMA
CREATE TABLE TMP_DISC_TURMA (
	TURMA T_CODIGO,
	DISCIPLINA T_CODIGO,
	ANO T_ANO,
	SEMESTRE T_SEMESTRE2
)
GO

SELECT TURMA, DISCIPLINA, ANO, SEMESTRE 
FROM LY_TURMA T
JOIN LY_CURSO C ON C.CURSO = T.CURSO
WHERE ANO = '2016' AND SEMESTRE = '0'
AND C.FACULDADE IN ('50','51','52')
GO

SELECT DISTINCT DISCIPLINA, ANO, SEMESTRE 
FROM LY_TURMA T
JOIN LY_CURSO C ON C.CURSO = T.CURSO
WHERE ANO = '2016' AND SEMESTRE = '0'
AND C.FACULDADE IN ('50','51','52')
GO



